import React, { useState, useEffect } from 'react'
export default function Create() {
  const [coins, setCoins] = useState([])

  const [form, setForm] = useState({
    email: '',
    target: '',
    coinType: '',
  })

  useEffect(() => {
    calldata()
  }, [])

  const calldata = async () => {
    const options = {
      method: 'GET',
    }

    fetch(
      `https://api.coingecko.com/api/v3/coins/markets?vs_currency=${currency}&ids=${coinName}&order=market_cap_desc&per_page=25&page=${page}&sparkline=false`,
      options,
    )
      .then((response) => response.json())
      .then((response) => {
        console.log(response)
        setCoins(response)
      })
      .catch((err) => console.error(err))
  }

  const setAlert = async (e) => {
    e.preventDefault()
    console.log(form)
    const { email, target, coinType } = form
    const res = await fetch('/track', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, target, coinType }),
    })

    const data = await res.json()
    // setSpinner(false);
    if (res.status === 400 || !data) {
      // toast.error(data.error[0].msg);
      console.log(res)
    } else {
      console.log(res)
      // toast.success(data.message);
      // navigate("/login", { replace: true });
    }
  }
  return (
    <div>
      <div className="container-fluid d-flex justify-content-center align-items-center form">
        <div className="container mt-5">
          <h1 className="text-center text-light mb-5">Track Your Target</h1>
          <form className="p-5 border">
            <div className="container p-5">
              <div className="container p-5">
                <input
                  type="email"
                  className="form-control tracker"
                  placeholder="Enter your email address"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                />
              </div>
              <div className="container d-flex justify-content-between  p-5">
                <input
                  type="text"
                  className="form-control tracker"
                  placeholder="Enter Your Target"
                  value={form.target}
                  onChange={(e) => setForm({ ...form, target: e.target.value })}
                />
                <select
                  id="select"
                  onChange={(e) =>
                    setForm({ ...form, coinType: e.target.value })
                  }
                >
                  <option id="option">Select Coin</option>
                  {coins.map((ele) => {
                    return (
                      <>
                        <option id="option" value={ele.symbol}>
                          {ele.symbol.toUpperCase()}
                        </option>
                      </>
                    )
                  })}
                </select>
              </div>
              <div className="d-flex justify-content-center">
                <button
                  className="btn btn-light"
                  type="submit"
                  onClick={setAlert}
                >
                  Set Alert
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}
