import React, { useContext, useEffect, useState } from 'react'
import Axios from 'axios'
import { Store } from './Store'
export default function Track() {
  const [track, setTrack] = useState([])
  const { state, dispatch: ctxDispatch } = useContext(Store)
  const {
    track: { alert },
    userInfo,
  } = state
  // useEffect(() => {
  //   fetchTrack()
  // }, [])

  // const fetchTrack = async () => {
  //   const { data } = await Axios.get('/track', {
  //     headers: {
  //       'Content-Type': 'application/json',
  //       authorization: `Bearer ${userInfo.data.token}`,
  //     },
  //   })
  //   if (data) {
  //     setTrack(data.data)
  //   }
  // }

  console.log(alert)

  const deleteAlert = async (id) => {
    const confirm = window.confirm(
      'Are you sure you want to delete this alert?',
    )
    if (confirm) {
      ctxDispatch({
        type: 'DELETE_ALERT',
        payload: { id },
      })
      const { data } = await Axios.delete(`/track/${id}`, {
        headers: {
          'Content-Type': 'application/json',
          authorization: `Bearer ${userInfo.data.token}`,
        },
      })
      if (data) {
        console.log(data.message)
      }
    }
  }

  return (
    <div>
      <h3 className="text-light text-center mt-5">My Targets</h3>
      <div className="container-fluid d-flex justify-content-center align-items-center">
        {alert.length === 0 ? (
          <>
            <h1 className="text-light text-center">No Track Found</h1>
          </>
        ) : (
          <>
            <table className="container mt-5 table tab">
              <thead>
                <th>Email</th>
                <th>Target</th>
                <th>CoinType</th>
                <th>Edit</th>
              </thead>
              <tbody>
                {alert.map((ele) => {
                  console.log(ele)
                  return (
                    <tr>
                      <td>{ele.email}</td>
                      <td>{ele.target}</td>
                      <td>{ele.coinType}</td>
                      <td className="d-flex justify-content-start">
                        <button className="btn btn-dark">Edit</button>
                        <button
                          className="btn btn-dark mx-3"
                          onClick={() => deleteAlert(ele._id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  )
                })}
              </tbody>
            </table>
          </>
        )}
      </div>
    </div>
  )
}
